package stringss;

public class concate {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String str1="Akshay";
		String str2="Dethe";
		
		String str3=str1+" "+str2;
		System.out.println(str3);
		
		String str4=str1+" ".concat(str2);
		System.out.println(str4);
	}

}
